var should = require( 'should' );
leitura_valida = require( '../leitura_valida.js' );



describe( 'temperatura', function() {

   it( 'Valor dentro do range temperatura retorna true ', function() {
     leitura_valida(15 ,3,50 ).should.be.true();
   });

   it( 'Valor antes do range temperatura retorna false', function() {
     leitura_valida(1 ,3,50 ).should.be.false();
   });

   it( 'Valor depois do range temperatura retorna false', function() {
     leitura_valida(1 ,3,50 ).should.be.false();
   });
});
